import React, { Component } from "react";
import Header from "../../components/Header/Header";
import Form from "../../components/Form/Form";
import axios from "axios";
import { API_HOST } from "../../api_utils";
import { useParams } from "react-router";
import { css } from "styled-components";
import classes from "./style.module.scss";
import { Helmet } from "react-helmet";
import MetaDecorator from "../../components/MetaDecorator/index.js";

export class newsPage extends Component {
  state = {
    data: {},
    html: "",
    css: "",
  };
  async componentDidMount() {
    const id = this.props.match.params.pageId;
    try {
      const response = await axios.get(`${API_HOST}/newsEdit/${id}/content`);

      this.setState({
        data: response.data,
        html: response.data[`${id}html`],
        css: response.data[`${id}css`],
      });

      console.log(this.state.html);
      console.log(this.state.css);
    } catch (error) {
      console.log("error :>>", error);
    }
  }

  render() {
    let formData = {
      title: "Бүртгүүлэх",
      inputs: [
        {
          label: "Багцын сонголт",
        },
        {
          label: "Таны нэр",
        },
        {
          label: "Имэйл хаяг",
        },
        {
          label: "Утасны дугаар",
        },
        {
          label: "Байгууллагын нэр",
        },
        {
          label: "Имэйл хэрэглэгчийн тоо",
        },
      ],
    };
    return (
      <div className="cont">
        <MetaDecorator
          title={"hey"}
          description={"this is test"}
          imageUrl="http://13.76.216.31:5000/static/img/news/%E2%80%9CInspire%202021%E2%80%9D%20%D0%B0%D1%80%D0%B3%D0%B0%20%D1%85%D1%8D%D0%BC%D0%B6%D1%8D%D1%8D%20%D3%A8%D0%BC%D0%BD%D3%A9%D0%B3%D0%BE%D0%B2%D1%8C%20%D0%B0%D0%B9%D0%BC%D0%B0%D0%B3%D1%82%20%D0%B0%D0%BC%D0%B6%D0%B8%D0%BB%D1%82%D1%82%D0%B0%D0%B9%20%D0%B7%D0%BE%D1%85%D0%B8%D0%BE%D0%BD%20%D0%B1%D0%B0%D0%B9%D0%B3%D1%83%D1%83%D0%BB%D0%B0%D0%B3%D0%B4%D0%BB%D0%B0%D0%B0/1637547860499Inspire.jpg"
        />

        <div
          className={classes.body}
          dangerouslySetInnerHTML={{ __html: this.state.html }}
        ></div>

        {/* <Form title={formData.title} inputs={formData.inputs} /> */}
        <style>{this.state.css}</style>
      </div>
    );
  }
}

export default newsPage;
