import React, { useState, useEffect } from "react";
import "bootstrap";
import { Table, FormGroup, Form, Col, Row } from "react-bootstrap";
import style from "./homeEdit.scss";

const HomeEdit = () => {
  const [image, setImage] = useState();
  const [isValid, setIsValid] = useState(true);
  const [pages, setPages] = useState([]);
  const [error, setError] = useState("");
  const [category, setCategory] = useState([]);
  const [body, setBody] = useState(false);
  const [modaldata, setModaldata] = useState([]);
  const [validated, setValidated] = useState(false);
  const handle = (e) => {
    const newbody = { ...body };

    if (e.target.id === "image") {
      const image = e.target.files[0];

      setImage(image);
      console.log("kkk", image);
    } else {
      newbody[e.target.id] = e.target.value;
      setBody(newbody);
    }
  };
  const submit = (e) => {
    const form = e.currentTarget;
    if (form.checkValidity() === false) {
      e.preventDefault();
      e.stopPropagation();
    }

    setValidated(true);

    if (form.checkValidity() === true) {
    }
  };
  return (
    <div className="container">
      <div className="col-12 mt-5">
        <div className="modal-header">
          <h5 className="modal-tittle" id="addPageModalLabel" Create Page>
            Баннэр
          </h5>
        </div>
        <Form noValidate validated={validated} onSubmit={submit}>
          <Row className="mb-3">
            <Form.Group as={Col} md="6">
              <Form.Label>Title</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Title of Banner"
                onChange={(e) => handle(e)}
                id="banner title visible"
              />
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="6">
              <Form.Label>Харагдах байдал</Form.Label>
              <Form.Select
                onChange={(e) => handle(e)}
                id="banner visible"
                required
              >
                <option value={0}>Сонгох</option>
                <option value={true}>Харуулна</option>
                <option value={false}>Харуулахгүй</option>
              </Form.Select>
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Row className="mb-3">
            <Form.Group as={Col} md="6">
              <Form.Label>Body</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Text"
                onChange={(e) => handle(e)}
                id="banner body"
              />
            </Form.Group>
            <Form.Group as={Col} md="6">
              <Form.Label>Харагдах байдал</Form.Label>
              <Form.Select
                onChange={(e) => handle(e)}
                required
                id="banner body visible"
              >
                <option value={0}>Сонгох</option>
                <option value={true}>Харуулна</option>
                <option value={false}>Харуулахгүй</option>
              </Form.Select>
              <Form.Control.Feedback type="invalid">
                Please provide a valid city.
              </Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Row className="mb-3">
            <Form.Group as={Col} md="6">
              <Form.Label>Link</Form.Label>
              <Form.Control
                onChange={(e) => handle(e)}
                id="banner capacity"
                type="text"
                required
                placeholder="Хүний тоо"
              />
              <Form.Control.Feedback type="invalid">
                Please provide a valid state.
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="6">
              <Form.Label>Зураг</Form.Label>
              <Form.Control
                required
                type="file"
                onChange={(e) => handle(e)}
                id="banner image"
              />
              <Form.Control.Feedback type="invalid">
                Зураг оруулна уу
              </Form.Control.Feedback>
            </Form.Group>
          </Row>

          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary btn-sm"
              onClick="clearForm()"
            >
              Clear
            </button>
            <button type="submit" className="btn btn-primary btn-sm">
              Submit
            </button>
          </div>
        </Form>
      </div>
    </div>
  );
};

export default HomeEdit;
